import React, { useEffect, useRef } from 'react';

const mapContainerStyle = {
  width: '100vw',
  height: '100vh',
};

const center = {
  lat: -34.397,
  lng: 150.644,
};

const customIcon = 'https://maps.google.com/mapfiles/ms/icons/blue-dot.png';

const MapWithClickableCustomMarkers = () => {
  const mapRef = useRef(null);

  useEffect(() => {
    const script = document.createElement('script');
    script.src = `https://maps.googleapis.com/maps/api/js?key=${process.env.GOOGLE_MAPS}&libraries=places`;
    script.async = true;
    script.defer = true;
    script.onload = () => {
      const map = new window.google.maps.Map(document.getElementById('map'), {
        center: center,
        zoom: 8,
      });

      map.addListener('click', (event) => {
        new window.google.maps.Marker({
          position: event.latLng,
          map: map,
          icon: customIcon,
        });
      });

      mapRef.current = map;
    };

    document.head.appendChild(script);

    return () => {
      if (script.parentNode) {
        script.parentNode.removeChild(script);
      }
    };
  }, []);

  return <div id="map" style={mapContainerStyle}></div>;
};

export default MapWithClickableCustomMarkers;


