import React, { useEffect, useRef } from 'react';

const mapContainerStyle = {
  width: '100vw',
  height: '100vh',
};

const center = {
  lat: -34.397,
  lng: 150.644,
};

const customIcon = '/house.png';

const MapWithClickableCustomMarkers = () => {
  const mapRef = useRef(null);

  useEffect(() => {
    const script = document.createElement('script');
    script.src = `https://maps.googleapis.com/maps/api/js?key=AIzaSyDF2rKGbY2nhUoe1rKcI3DhUKM_HZu2oUY&libraries=places`;
    script.async = true;
    script.defer = true;
    script.onload = () => {
      const map = new window.google.maps.Map(document.getElementById('map'), {
        center: center,
        zoom: 8,
      });

      const service = new window.google.maps.places.PlacesService(map);

      map.addListener('click', (event) => {
        //const request = `https://maps.googleapis.com/maps/api/place/nearbysearch/json?&location=${ event.latLng }&radius=100&types=establishment&key=AIzaSyDF2rKGbY2nhUoe1rKcI3DhUKM_HZu2oUY`;
        const request = {
            location: event.latLng,
            radius: '50', // search within 50 meter
          };

          service.nearbySearch(request, (results, status) => {
            if (status === window.google.maps.places.PlacesServiceStatus.OK && results.length > 0) {
                results.sort((a, b) => {
                    const distanceA = window.google.maps.geometry.spherical.computeDistanceBetween(event.latLng, a.geometry.location);
                    const distanceB = window.google.maps.geometry.spherical.computeDistanceBetween(event.latLng, b.geometry.location);
                    return distanceA - distanceB;
                  });


              const place = results[0];
              
              new window.google.maps.Marker({
                position: place.geometry.location,
                map: map,
                icon: customIcon,
                title: place.name,
              });
  
              console.log('Place details:', place);
            } else {
              console.log('No places found at this location');
            }
          });
      });

      mapRef.current = map;
    };

    document.head.appendChild(script);

    return () => {
      if (script.parentNode) {
        script.parentNode.removeChild(script);
      }
    };
  }, []);

  return <div id="map" style={mapContainerStyle}></div>;
};

export default MapWithClickableCustomMarkers;


